@use 'vars/variables';
@use 'vars/mixins' as mixins;
@use 'sections/about';
@use 'sections/contact';
@use 'sections/home';
@use 'sections/services';
@use 'menu';

html {
  scroll-behavior: smooth;
}

body {
  margin: 0;
  padding: 0;
  overflow-x: hidden;
  @include mixins.flex;
  flex-flow: column wrap;
}

* {
  box-sizing: border-box;
  font-family: "Montserrat", sans-serif;
  -webkit-tap-highlight-color: transparent;
}

*:focus {
  box-shadow: 0 0 2px 1px var(--primary-color);
  outline: 0 none;
}

.wrapper {
  max-width: 100rem;
  position: relative;
  @include mixins.flex;
  flex-flow: column wrap;
}

.arrow-up {
  position: fixed;
  z-index: 100;
  bottom: 20px;
  right: 30px;
  transform: rotate(-90deg);
  color: gray;
  cursor: pointer;
  font-size: 2.5rem;

  &:hover {
    color: var(--primary-active);
    transition: 0.3s;
  }
}

.circle {
  position: absolute;
  border-radius: 50%;
}

