        background-color: rgba(var(--primary-color-rgb), 0.2);
      }

      &:hover {
        color: var(--primary-active);
      }

      &--active {
        color: var(--primary-active);
      }

      &__icon {
        font-size: 1.3rem;
        margin-bottom: 4px;
      }

      &__label {
        text-transform: uppercase;
        font-size: 0.75rem;
      }
    }
  }
}

@media all and (min-width: 750px) {
  .nav {
    top: 0;
    height: 52px;
    @include mixins.flex(space-between);
    box-shadow: 0px 2px 16px 5px rgba(0, 0, 0, 0.26);
    padding: 0 2rem;

    &__logo {
      display: block;
      text-transform: uppercase;
      color: var(--primary-active);
      font-size: 1.4rem;
    }

    .menu {
      @include mixins.flex(flex-end);

      &__option {
        padding: 1rem 1.4rem;

        &--active::after {
          position: absolute;
          content: "";
          width: 100%;
          height: 3px;
          bottom: 0;
          left: 0;
          background-color: var(--primary-active);
        }

        &__icon {
          display: none;
        }

        &__label {
          font-size: 1.05rem;
        }
      }
    }
  }
}

